mapper.select:
SELECT COUNT(meet_num) FROM meeting WHERE 1=1
and meet_state=#{param1}
and meet_subject LIKE concat('%', #{param2}, '%')

select meet_num,meet_subject,meet_start,meet_end,meet_interest,meet_region,meet_point,meet_state from meeting
WHERE 1=1

	and meet_state=#{param3}


	and meet_subject LIKE concat('%', #{param4}, '%')

order by meet_num desc
LIMIT #{param1} OFFSET #{param2}

SELECT COUNT(ad_num) FROM advertisement
where 1=1

	and ad_state=#{ad_meetstate}

select a.meet_subject, a.meet_gatherStart, a.meet_gatherEnd, b.ad_num, b.ad_meetArea, b.ad_state from meeting as a inner join advertisement as b on a.meet_num =b.meet_num
and ad_state=#{param3}
order by b.ad_num desc LIMIT #{param1} OFFSET #{param2}

select m.mem_id, m.mem_name, m.mem_email, m.mem_phone, m.mem_birth, m.mem_gender, m.mem_state, (select count(s.sct_num) from sanctions s where s.mem_id = m.mem_id and s.sct_type=0) as sct_num, (select sum(p.pt_count) from point p where p.mem_id = m.mem_id) as pt_count from members m where 1 = 1
and mem_id LIKE concat('%', #{param3}, '%')
order by mem_date desc LIMIT #{param1} OFFSET #{param2}
